+package com.chris.m3usuite.telegram
+
+/**

Simple best-effort parser for Telegram captions / filenames.
Extracts: normalized title, year, SxxEyy (season/episode), language, quality.
This is intentionally lightweight and resilient; it should never throw.
*/
+object TelegramHeuristics {
data class Parsed(
   val title: String,
   val year: Int? = null,
   val season: Int? = null,
   val episode: Int? = null,
   val language: String? = null,
   val quality: String? = null
)
private val yearRegex = Regex("(?<!\d)(19\d{2}|20\d{2})(?!\d)")
private val seRegex = Regex("(?i)(S(\d{1,2}))[ ._-]*E(\d{1,2})")
private val qualityRegex = Regex("(?i)\b(2160p|1080p|720p|4k|hdr|dv|webrip|web-dl|blu[- ]?ray|x264|x265|hevc|remux)\b")
private val langTokens = mapOf(
   "GERMAN" to "de", "GER" to "de", "DEUTSCH" to "de", "DE" to "de",
   "ENGLISCH" to "en", "ENGLISH" to "en", "ENG" to "en", "EN" to "en",
   "MULTI" to "multi", "DUAL" to "multi",
   "DUBBED" to "dub", "SUBBED" to "sub"
)
private val junkTokens = setOf(
   "x264","x265","hevc","aac","dts","ddp","dd+","hdr","dv","atmos",
   "multi","dubbed","subbed","webrip","web-dl","bluray","blu-ray","remux",
   "repack","proper","readnfo","uncut","extended","remastered","german","deutsch","english","eng","ger"
)
fun parse(raw: String?): Parsed {
   if (raw.isNullOrBlank()) return Parsed(title = "Telegram")
   var s = raw.replace('_', ' ').replace('.', ' ')
   s = Regex("\s+").replace(s, " ").trim()
   // Year
   val year = yearRegex.find(s)?.value?.toIntOrNull()
   // Season/Episode
   val seMatch = seRegex.find(s)
   val season = seMatch?.groups?.get(2)?.value?.toIntOrNull()
   val episode = seMatch?.groups?.get(3)?.value?.toIntOrNull()
   // Quality (first match)
   val quality = qualityRegex.findAll(s).map { it.value.lowercase() }.firstOrNull()
   // Language (first matching token)
   var lang: String? = null
   val upper = s.uppercase()
   for ((k, v) in langTokens) {
       if (upper.contains(k)) { lang = v; break }
   }
   // Build cleaned title
   var tokens = s.split(' ')
   tokens = tokens.filterNot { t ->
       val tt = t.lowercase()
       tt in junkTokens || qualityRegex.matches(t) || yearRegex.matches(t) || seRegex.matches(t)
   }
   // Remove trailing/leading brackets or dashes
   var title = tokens.joinToString(" ").trim(' ', '-', '.', '[', ']', '(', ')')
   if (title.isBlank()) title = raw.trim()
   return Parsed(
       title = title,
       year = year,
       season = season,
       episode = episode,
       language = lang,
       quality = quality
   )
}
+}
