+package com.chris.m3usuite.data.repo
+
+import android.content.Context
+import com.chris.m3usuite.data.obx.ObxStore
+import com.chris.m3usuite.data.obx.ObxTelegramMessage
+import com.chris.m3usuite.model.MediaItem
+import com.chris.m3usuite.prefs.SettingsStore
+import kotlinx.coroutines.Dispatchers
+import kotlinx.coroutines.withContext
+
+/**

Provides per-chat, newest-first lists of Telegram items mapped to MediaItem for UI rows.
Only minimal enrichment is done here; full details can be resolved on-demand.
*/
+class TelegramContentRepository(
private val context: Context,
private val settings: SettingsStore
+) {
private val box by lazy { ObxStore.get(context) }
suspend fun recentVodByChat(chatId: Long, limit: Int = 60, offset: Int = 0): List =
   recentByChatInternal(chatId, "vod", limit, offset)
suspend fun recentSeriesByChat(chatId: Long, limit: Int = 60, offset: Int = 0): List =
   recentByChatInternal(chatId, "series", limit, offset)
private suspend fun recentByChatInternal(chatId: Long, type: String, limit: Int, offset: Int): List =
   withContext(Dispatchers.IO) {
       if (chatId == 0L) return@withContext emptyList()
       val qb = box.boxFor(ObxTelegramMessage::class.java).query(
           com.chris.m3usuite.data.obx.ObxTelegramMessage_.chatId.equal(chatId)
       ).build()
       val rows = qb.find().sortedWith(
           compareByDescending<ObxTelegramMessage> { it.date ?: 0L }
               .thenByDescending { it.messageId }
       ).drop(offset).take(limit)
       rows.map { it.toMediaItem(type) }
   }
private fun ObxTelegramMessage.toMediaItem(type: String): MediaItem {
   val caption = caption ?: "Telegram $messageId"
   val parsed = com.chris.m3usuite.telegram.TelegramHeuristics.parse(caption)
   val titleBase = if (parsed.title.isNotBlank()) parsed.title else caption
   val cleanTitle = buildString {
       append(titleBase)
       if (type == "series" && parsed.season != null && parsed.episode != null) {
           append(" S%02dE%02d".format(parsed.season, parsed.episode))
       }
       if (parsed.year != null) append(" (${parsed.year})")
   }.trim()
   val encodedId = 5_000_000_000_000L + (this.id)
   return MediaItem(
       id = encodedId,
       type = type,
       streamId = null,
       name = cleanTitle,
       sortTitle = titleBase.lowercase(),
       categoryId = null,
       categoryName = null,
       logo = null,
       poster = null,
       backdrop = null,
       epgChannelId = null,
       year = parsed.year,
       rating = null,
       durationSecs = this.durationSecs,
       plot = caption,
       url = null,
       extraJson = null,
       source = "TG",
       tgChatId = this.chatId,
       tgMessageId = this.messageId,
       tgFileId = this.fileId,
       images = emptyList(),
       imdbId = null,
       tmdbId = null,
       trailer = null,
       director = null,
       cast = null,
       country = null,
       releaseDate = null,
       genre = null,
       containerExt = null,
       providerKey = null,
       genreKey = null,
   )
}
+}
